import React, { useEffect, useState } from 'react';

export default function ProductTable() {
  const [products, setProducts] = useState([]); // State to hold product data
  const [loading, setLoading] = useState(true); // State to manage loading status
  const [error, setError] = useState(null); // State to manage errors

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/getproducts');
        if (!response.ok) {
          throw new Error('Failed to fetch products');
        }
        const data = await response.json();
        setProducts(data); // Assuming the API returns an array of products
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false); // Set loading to false once data is fetched
      }
    };

    fetchProducts();
  }, []);

  if (loading) {
    return <div className="text-center mt-4">Loading...</div>; // Loading message while fetching
  }

  if (error) {
    return <div className="text-danger text-center mt-4">Error: {error}</div>; // Display error if any
  }

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">Product List</h2>
      <table className="table table-striped table-bordered">
        <thead className="thead-dark">
          <tr>
            <th>PNAME</th>
            <th>QUANTITY</th>
            <th>PRICE</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product, index) => (
            <tr key={index}>
              <td>{product.pname}</td> {/* Assuming the API returns pname */}
              <td>{product.quantity}</td> {/* Assuming the API returns quantity */}
              <td>â‚¹{product.price}</td> {/* Assuming the API returns price */}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
