import React, { useEffect, useState } from 'react';

export default function ShowSearch() {
    const [brands, setBrands] = useState([]);    // State to store brands
    const [categories, setCategories] = useState([]); // State to store categories
    const [selectedBrand, setSelectedBrand] = useState(""); // State to track selected brand
    const [selectedCategory, setSelectedCategory] = useState(""); // State to track selected category

    useEffect(() => {
        // Fetch brands from the API when the component mounts
        fetch('http://localhost:5000/api/getbrands')
            .then(response => response.json())
            .then(data => setBrands(data))  // Update the state with fetched data
            .catch(error => console.error('Error fetching brands:', error));

        // Fetch categories from the API when the component mounts
        fetch('http://localhost:5000/api/getcategories')
            .then(response => response.json())
            .then(data => setCategories(data)) // Update the state with fetched data
            .catch(error => console.error('Error fetching categories:', error));
    }, []);

    return (
        <div className='w-100 d-flex justify-content-center align-items-center flex-column'>
            <div className='d-flex justify-content-center align-items-center flex-row w-100 '>
                
                {/* Dropdown for Brands */}
                <div className='d-flex justify-content-center align-items-center mb-3 w-50 mx-1'>
                    <select
                        className="form-select w-100"
                        aria-label="Select Brand"
                        value={selectedBrand}
                        onChange={(e) => setSelectedBrand(e.target.value)}
                    >
                        <option value="">Select Brand</option>
                        {brands.map((brand) => (
                            <option
                                key={brand.id}
                                value={brand.id}
                                style={{ backgroundImage: `url(${brand.logo})`, backgroundRepeat: 'no-repeat', paddingLeft: '30px' }}
                            >
                                {brand.bname}
                            </option>
                        ))}
                    </select>
                </div>

                {/* Dropdown for Categories */}
                <div className='d-flex justify-content-center align-items-center mb-3 w-50 mx-1'>
                    <select
                        className="form-select w-100"
                        aria-label="Select Category"
                        value={selectedCategory}
                        onChange={(e) => setSelectedCategory(e.target.value)}
                    >
                        <option value="">Select Category</option>
                        {categories.map((category) => (
                            <option
                                key={category.id}
                                value={category.id}
                                style={{ backgroundImage: `url(${category.logo})`, backgroundRepeat: 'no-repeat', paddingLeft: '30px' }}
                            >
                                {category.cname}
                            </option>
                        ))}
                    </select>
                </div>
            </div>

            <button className='btn btn-dark my-2 d-flex justify-content-center align-items-center'>
                <span className="material-symbols-outlined">search</span> Search
            </button>
        </div>
    );
}
