import React, { useState } from 'react';
import CloseIcon from '@mui/icons-material/Close';

export default function ProductCard({ pname, bname, cname, price,pimage }) {
  const [showOverlay, setShowOverlay] = useState(false);
  const [sellingPrice, setSellingPrice] = useState('');
  const [salesperson, setSalesperson] = useState('Kishan');

  const handleSellClick = () => setShowOverlay(true);
  const handleCloseOverlay = () => setShowOverlay(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const today = new Date().toLocaleDateString('en-GB'); // Format: dd/mm/yyyy

    const saleData = {
      pname,
      bname,
      cname,
      sellingprice: sellingPrice,
      dos: today,
      salesperson,
    };

    try {
      const response = await fetch('http://localhost:5000/api/sale', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(saleData),
      });

      if (response.ok) {
        console.log('Sale recorded successfully!');
        handleCloseOverlay(); // Close overlay on success
      } else {
        console.error('Failed to record the sale');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div style={{ position: 'relative', width: '180px',height:"180px", margin: '20px auto' }}>
      <div className="card" style={cardStyle}>
        <img
          className="card-img-top"
          src={pimage}
          alt="Product"
          style={{width:"180px",height:"100px"}}
        />
        <div className="card-body d-flex justify-content-center align-items-center ">
          <h5 className="card-title">{pname}</h5>
          <h6 className="card-text">₹{price}</h6>
          <button className="btn btn-primary" onClick={handleSellClick}>
            Sell
          </button>
        </div>
      </div>

      {showOverlay && (
        <div style={overlayStyle}>
          <div style={formContainerStyle}>
            <CloseIcon style={closeIconStyle} onClick={handleCloseOverlay} />
            <h3>Sell {pname}</h3>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Price Sold</label>
                <input
                  type="number"
                  className="form-control"
                  value={sellingPrice}
                  onChange={(e) => setSellingPrice(e.target.value)}
                  required
                />
              </div>

              <div className="form-group">
                <label>Who Sold</label>
                <select
                  className="form-control"
                  value={salesperson}
                  onChange={(e) => setSalesperson(e.target.value)}
                >
                  <option value="Kishan">Kishan</option>
                  <option value="Ajay">Ajay</option>
                  <option value="Bacha">Bacha</option>
                  <option value="Jitendra">Jitendra</option>
                  <option value="Others">Others</option>
                </select>
              </div>

              <button type="submit" className="btn btn-success mt-3">
                Submit
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

// Styles
const cardStyle = {
  boxShadow: '0 4px 8px 0 rgba(0, 0, 0, 0.2)',
  transition: '0.3s',
  borderRadius: '10px',
};

const overlayStyle = {
  position: 'fixed',
  top: 0,
  left: 0,
  width: '100%',
  height: '100%',
  backgroundColor: 'rgba(0, 0, 0, 0.7)',
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  zIndex: 1000,
};

const formContainerStyle = {
  backgroundColor: 'white',
  padding: '30px',
  borderRadius: '10px',
  width: '400px',
  position: 'relative',
  boxShadow: '0 0 15px rgba(0, 0, 0, 0.3)',
};

const closeIconStyle = {
  position: 'absolute',
  top: '10px',
  right: '10px',
  cursor: 'pointer',
};
