import React, { useEffect, useState } from 'react';
import ProductCard from './ProductCard'; // Ensure you have this component created

export default function ShowSearch() {
    const [brands, setBrands] = useState([]);    
    const [categories, setCategories] = useState([]); 
    const [selectedBrand, setSelectedBrand] = useState(null); 
    const [selectedCategory, setSelectedCategory] = useState(null); 
    const [loading, setLoading] = useState(false); // State for loading
    const [products, setProducts] = useState([]); // State for products

    useEffect(() => {
        // Fetch brands from the API
        fetch('http://localhost:5000/api/getbrands')
            .then(response => response.json())
            .then(data => setBrands(data))
            .catch(error => console.error('Error fetching brands:', error));

        // Fetch categories from the API
        fetch('http://localhost:5000/api/getcategories')
            .then(response => response.json())
            .then(data => setCategories(data))
            .catch(error => console.error('Error fetching categories:', error));
    }, []);

    const handleSearch = () => {
        setLoading(true); // Set loading state to true

        // Prepare the request body
        const bname = selectedBrand ? selectedBrand.bname : "All"; // If selectedBrand is null, use "All"
        const cname = selectedCategory ? selectedCategory.cname : "All"; // If selectedCategory is null, use "All"

        fetch('http://localhost:5000/getproducts', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ bname, cname }),
        })
        .then(response => response.json())
        .then(data => {
            setProducts(data); // Set the products from the response
            setLoading(false); // Set loading state to false
        })
        .catch(error => {
            console.error('Error fetching products:', error);
            setLoading(false); // Set loading state to false in case of error
        });
    };

    return (
        <div className='w-100 d-flex justify-content-center align-items-center flex-column'>
            <div className='d-flex justify-content-center align-items-center flex-column w-100'>
                
                {/* Custom Dropdown for Brands */}
                <div className='d-flex justify-content-center align-items-center mb-3 w-100 mx-1'>
                    <div className="dropdown w-100" style={{ position: 'relative' }}>
                        <button
                            className="w-100 btn btn-light dropdown-toggle"
                            type="button"
                            id="brandDropdown"
                            data-bs-toggle="dropdown"
                            aria-expanded="false"
                            disabled={loading} // Disable button during loading
                        >
                            {selectedBrand ? (
                                <div className="d-flex align-items-center">
                                    <img
                                        src={selectedBrand.logo}
                                        alt={`${selectedBrand.bname} logo`}
                                        style={{ width: "80px", height: "60px", marginRight: "10px" }}
                                    />
                                    {selectedBrand.bname}
                                </div>
                            ) : (
                                "Select Brand"
                            )}
                        </button>
                        <ul 
                            className="dropdown-menu" 
                            aria-labelledby="brandDropdown"
                            style={{
                                position: 'absolute',
                                width: '100%',
                                left: 0,
                                top: '100%',
                                zIndex: 1000,
                            }}
                        >
                            {/* All option */}
                            <li 
                                onClick={() => setSelectedBrand(null)} 
                                className="dropdown-item d-flex align-items-center"
                                style={{ cursor: 'pointer' }}
                            >
                                <img
                                    src="" // Placeholder image or use a default image
                                    alt="All brands"
                                    style={{ width: "80px", height: "60px", marginRight: "10px" }}
                                />
                                All
                            </li>
                            {brands.map((brand) => (
                                <li 
                                    key={brand.id} 
                                    onClick={() => setSelectedBrand(brand)} 
                                    className="dropdown-item d-flex align-items-center"
                                    style={{ cursor: 'pointer' }}
                                >
                                    <img
                                        src={brand.logo}
                                        alt={`${brand.bname} logo`}
                                        style={{ width: "80px", height: "60px", marginRight: "10px" }}
                                    />
                                    {brand.bname}
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>

                {/* Custom Dropdown for Categories */}
                <div className='d-flex justify-content-center align-items-center mb-3 w-100 mx-1'>
                    <div className="dropdown w-100" style={{ position: 'relative' }}>
                        <button
                            className="w-100 btn btn-light dropdown-toggle"
                            type="button"
                            id="categoryDropdown"
                            data-bs-toggle="dropdown"
                            aria-expanded="false"
                            disabled={loading} // Disable button during loading
                        >
                            {selectedCategory ? (
                                <div className="d-flex align-items-center">
                                    <img
                                        src={selectedCategory.logo}
                                        alt={`${selectedCategory.cname} logo`}
                                        style={{ width: "80px", height: "60px", marginRight: "10px" }}
                                    />
                                    {selectedCategory.cname}
                                </div>
                            ) : (
                                "Select Category"
                            )}
                        </button>
                        <ul 
                            className="dropdown-menu" 
                            aria-labelledby="categoryDropdown"
                            style={{
                                position: 'absolute',
                                width: '100%',
                                left: 0,
                                top: '100%',
                                zIndex: 1000,
                            }}
                        >
                            {/* All option */}
                            <li 
                                onClick={() => setSelectedCategory(null)} 
                                className="dropdown-item d-flex align-items-center"
                                style={{ cursor: 'pointer' }}
                            >
                                <img
                                    src="" // Placeholder image or use a default image
                                    alt="All categories"
                                    style={{ width: "80px", height: "60px", marginRight: "10px" }}
                                />
                                All
                            </li>
                            {categories.map((category) => (
                                <li 
                                    key={category.id} 
                                    onClick={() => setSelectedCategory(category)} 
                                    className="dropdown-item d-flex align-items-center"
                                    style={{ cursor: 'pointer' }}
                                >
                                    <img
                                        src={category.logo}
                                        alt={`${category.cname} logo`}
                                        style={{ width: "80px", height: "60px", marginRight: "10px" }}
                                    />
                                    {category.cname}
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>
            </div>
            <button 
                className='btn btn-dark my-2 d-flex justify-content-center align-items-center'
                onClick={handleSearch} // Call handleSearch on click
                disabled={loading} // Disable button during loading
            >
                {loading ? <span>Loading...</span> : <span className="material-symbols-outlined">search</span>} Search
            </button>

            {/* Display Products */}
            <div className='d-flex flex-wrap justify-content-center'>
                {products.map(product => (
                    <ProductCard 
                        key={product._id} 
                        pname={product.pname} 
                        cname={product.cname} 
                        bname={product.bname} 
                        pimage={product.logo} // Change this if you have a different image field
                        price={product.price} 
                    />
                ))}
            </div>
        </div>
    );
}
