import React, { useState } from 'react';

export default function EditProductCard({ pname, cname, bname, pimage, price, pid, quantity, onUpdateProduct }) {
    const [newPrice, setNewPrice] = useState(price);
    const [newQuantity, setNewQuantity] = useState(quantity);
    const [loading, setLoading] = useState(false);
    const [showOverlay, setShowOverlay] = useState(false);

    const handleEdit = () => {
        setShowOverlay(true);
    };

    const handleCloseOverlay = () => {
        setShowOverlay(false);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setLoading(true);

        const updatedProduct = {
            _id: pid,
            pname,
            cname,
            bname,
            logo: pimage,
            price: newPrice,
            quantity: newQuantity
        };

        fetch('http://localhost:5000/api/editproduct', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(updatedProduct),
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                onUpdateProduct(updatedProduct); // Update the product in the parent
                alert('Product updated successfully!');
                handleCloseOverlay();
            })
            .catch(error => {
                console.error('Error updating product:', error);
                alert('Error updating product.');
            })
            .finally(() => {
                setLoading(false);
            });
    };

    return (
        <div className='card' style={{ width: '18rem', padding: '10px' }}>
            <img src={pimage} className='card-img-top' alt={`${pname} image`} />
            <div className='card-body'>
                <h5 className='card-title'>{pname}</h5>
                <p className='card-text'>Category: {cname}</p>
                <p className='card-text'>Brand: {bname}</p>
                <p className='card-text'>Price: â‚¹{price}</p>
                <p className='card-text'>Quantity: {quantity}</p>
                <button className='btn btn-primary' onClick={handleEdit}>
                    Edit
                </button>
            </div>

            {showOverlay && (
                <div className='overlay'>
                    <div className='overlay-content'>
                        <h5>Edit Product</h5>
                        <form onSubmit={handleSubmit}>
                            <div className="mb-3">
                                <label htmlFor="quantity" className="form-label">Quantity</label>
                                <input
                                    type="number"
                                    className="form-control"
                                    id="quantity"
                                    value={newQuantity}
                                    onChange={(e) => setNewQuantity(e.target.value)}
                                    required
                                />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="price" className="form-label">Price</label>
                                <input
                                    type="number"
                                    className="form-control"
                                    id="price"
                                    value={newPrice}
                                    onChange={(e) => setNewPrice(e.target.value)}
                                    required
                                />
                            </div>
                            <button type="submit" className="btn btn-success" disabled={loading}>
                                {loading ? (
                                    <span className="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span>
                                ) : (
                                    'Update'
                                )}
                            </button>
                            <button type="button" className="btn btn-danger ms-2" onClick={handleCloseOverlay}>
                                Close
                            </button>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
}
