import React, { useState } from 'react';

export default function EditProductCard({ pname, bname, cname, price, quantity, pimage, pid }) {
    const validPrice = typeof price === 'object' ? price.$numberDecimal : price;
    const validQuantity = typeof quantity === 'object' ? quantity.$numberDecimal : quantity;
  const [showOverlay, setShowOverlay] = useState(false);
  const [newPrice, setNewPrice] = useState(validPrice);
  const [newQuantity, setNewQuantity] = useState(validQuantity);
  const [loading, setLoading] = useState(false);

  const handleEditClick = () => setShowOverlay(true);

  const handleCloseOverlay = () => {
    setShowOverlay(false);
    setNewPrice(price); // Reset to initial price
    setNewQuantity(quantity); // Reset to initial quantity
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const updateData = {
      pid: String(pid),
      quantity: newQuantity,
      price: newPrice,
    };

    setLoading(true);

    try {
      const response = await fetch('http://localhost:5000/api/editproduct', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(updateData),
      });

      const responseText = await response.text();

      if (response.ok) {
        alert('Product updated successfully!');
        handleCloseOverlay();
      } else {
        alert(`Failed to update the product: ${responseText}`);
      }
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred while updating the product.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ position: 'relative', width: '160px' }}>
      <div className="card" style={cardStyle}>
        <img
          className="card-img-top"
          src={pimage}
          alt="Product"
          style={{ width: '160px', height: '100px', objectFit: 'contain' }}
        />
        <div className="card-body d-flex flex-column" style={{ flex: '1', height: '150px' }}>
          <p className="card-title text-align-center mb-2" style={titleStyle}>{pname}</p>
          <p className="card-text text-align-center my-0 bg-dark">₹{validPrice}</p>{/* Use validPrice here */}
          <p className="card-text text-align-center mb-3">Available-{quantity}</p>
          <button className="btn btn-primary" onClick={handleEditClick}>
            Edit
          </button>
        </div>
      </div>
  
      {showOverlay && (
        <div style={overlayStyle}>
          <div style={formContainerStyle}>
            <button style={closeButtonStyle} onClick={handleCloseOverlay}>X</button>
            <h3>Edit {pname}</h3>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Quantity</label>
                <input
                  type="number"
                  className="form-control"
                  value={newQuantity}
                  onChange={(e) => setNewQuantity(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <label>Price</label>
                <input
                  type="number"
                  className="form-control"
                  value={newPrice}
                  onChange={(e) => setNewPrice(e.target.value)}
                  required
                />
              </div>
              <button type="submit" className="btn btn-success mt-3" disabled={loading}>
                {loading ? 'Loading...' : 'Submit'}
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
  
}

// Styles
// Styles
const cardStyle = {
    boxShadow: '0 4px 8px 0 rgba(0, 0, 0, 0.2)',
    transition: '0.3s',
    borderRadius: '10px',
    height: '290px', // Set a fixed height for the card
    display: 'flex',
    flexDirection: 'column',
  };
  
  const overlayStyle = {
    position: 'fixed',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    backgroundColor: 'rgba(0, 0, 0, 0.7)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 1000,
  };
  
  const formContainerStyle = {
    backgroundColor: 'white',
    padding: '30px',
    borderRadius: '10px',
    width: '400px',
    position: 'relative',
    boxShadow: '0 0 15px rgba(0, 0, 0, 0.3)',
  };
  
  const closeButtonStyle = {
    position: 'absolute',
    top: '10px',
    right: '10px',
    backgroundColor: 'transparent',
    border: 'none',
    fontSize: '20px',
    cursor: 'pointer',
  };
  const titleStyle={
    textAlign: "center",
    fontSize: "0.9rem", // Font size
    overflow: "hidden",
    textOverflow: "ellipsis",
    whiteSpace: "normal", // Allow text to wrap
    marginBottom: 'auto', // Push button to the bottom
  }
