import React, { useEffect, useState } from 'react';

export default function ShowSearch() {
    const [brands, setBrands] = useState([]);  // State to store brands

    useEffect(() => {
        // Fetch brands from the API when the component mounts
        fetch('http://localhost:5000/api/getbrands')
            .then(response => response.json())
            .then(data => {
                setBrands(data);  // Update the state with fetched data
            })
            .catch(error => console.error('Error fetching brands:', error));
    }, []);

    return (
        <div className='d-flex justify-content-center align-items-center flex-column'>
            <div className='d-flex justify-content-center align-items-center'>
                <select className="form-select" aria-label="Select Brand">
                    {brands.map((brand) => (
                        <option key={brand.id} value={brand.id}>
                            {brand.bname}
                        </option>
                    ))}
                </select>
            </div>
            <button className='btn btn-dark my-2 d-flex justify-content-center align-items-center'>
                <span className="material-symbols-outlined">search</span> Search
            </button>
        </div>
    );
}
