import React, { useEffect, useState } from 'react';
import ProductCard from './ProductCard'; // Ensure this path is correct

export default function ShowSearch() {
    const [brands, setBrands] = useState([]);
    const [categories, setCategories] = useState([]);
    const [selectedBrand, setSelectedBrand] = useState(null); // Keep this as null for "no selection"
    const [selectedCategory, setSelectedCategory] = useState(null); // Keep this as null for "no selection"
    const [loading, setLoading] = useState(false);
    const [products, setProducts] = useState([]);

    useEffect(() => {
        // Fetch brands from the API
        fetch('http://localhost:5000/api/getbrands')
            .then(response => response.json())
            .then(data => setBrands(data))
            .catch(error => console.error('Error fetching brands:', error));

        // Fetch categories from the API
        fetch('http://localhost:5000/api/getcategories')
            .then(response => response.json())
            .then(data => setCategories(data))
            .catch(error => console.error('Error fetching categories:', error));
    }, []);

    const handleSearch = () => {
        // Check if neither brand nor category is selected
        if (!selectedBrand && !selectedCategory) {
            alert("Please select a brand and a category.");
            return;
        }

        setLoading(true);
        const requestBody = {
            bname: selectedBrand && selectedBrand.bname ? selectedBrand.bname : "All", // Handle "All"
            cname: selectedCategory && selectedCategory.cname ? selectedCategory.cname : "All" // Handle "All"
        };

        fetch('http://localhost:5000/api/getproducts', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(requestBody),
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                setProducts(data);
                setLoading(false);
            })
            .catch(error => {
                console.error('Error fetching products:', error);
                setLoading(false);
            });
    };

    return (
        <div className='w-100 d-flex justify-content-center align-items-center flex-column'>
            <div className='d-flex justify-content-center align-items-center flex-column w-100'>
                {/* Custom Dropdown for Brands */}
                <div className='d-flex justify-content-center align-items-center mb-3 w-100 mx-1'>
                    <div className="dropdown w-100" style={{ position: 'relative' }}>
                        <button
                            className="w-100 btn btn-light dropdown-toggle"
                            type="button"
                            id="brandDropdown"
                            data-bs-toggle="dropdown"
                            aria-expanded="false"
                        >
                            {selectedBrand ? (
                                <div className="d-flex align-items-center">
                                    <img
                                        src={selectedBrand.logo}
                                        alt={`${selectedBrand.bname} logo`}
                                        style={{ width: "80px", height: "60px", marginRight: "10px" }}
                                    />
                                    {selectedBrand.bname}
                                </div>
                            ) : (
                                "Select Brand"
                            )}
                        </button>
                        <ul className="dropdown-menu" aria-labelledby="brandDropdown">
                            {/* "All" Option */}
                            <li onClick={() => setSelectedBrand({ bname: "all", logo: '' })} className="dropdown-item" style={{ cursor: 'pointer' }}>
                                All Brands
                            </li>
                            {brands.map((brand) => (
                                <li
                                    key={brand.id}
                                    onClick={() => setSelectedBrand(brand)}
                                    className="dropdown-item d-flex align-items-center"
                                    style={{ cursor: 'pointer' }}
                                >
                                    <img
                                        src={brand.logo}
                                        alt={`${brand.bname} logo`}
                                        style={{ width: "80px", height: "60px", marginRight: "10px" }}
                                    />
                                    {brand.bname}
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>

                {/* Custom Dropdown for Categories */}
                <div className='d-flex justify-content-center align-items-center mb-3 w-100 mx-1'>
                    <div className="dropdown w-100" style={{ position: 'relative' }}>
                        <button
                            className="w-100 btn btn-light dropdown-toggle"
                            type="button"
                            id="categoryDropdown"
                            data-bs-toggle="dropdown"
                            aria-expanded="false"
                        >
                            {selectedCategory ? (
                                <div className="d-flex align-items-center">
                                    <img
                                        src={selectedCategory.logo}
                                        alt={`${selectedCategory.cname} logo`}
                                        style={{ width: "80px", height: "60px", marginRight: "10px" }}
                                    />
                                    {selectedCategory.cname}
                                </div>
                            ) : (
                                "Select Category"
                            )}
                        </button>
                        <ul className="dropdown-menu" aria-labelledby="categoryDropdown">
                            {/* "All" Option */}
                            <li onClick={() => setSelectedCategory({ cname: "all", logo: '' })} className="dropdown-item" style={{ cursor: 'pointer' }}>
                                All Categories
                            </li>
                            {categories.map((category) => (
                                <li
                                    key={category.id}
                                    onClick={() => setSelectedCategory(category)}
                                    className="dropdown-item d-flex align-items-center"
                                    style={{ cursor: 'pointer' }}
                                >
                                    <img
                                        src={category.logo}
                                        alt={`${category.cname} logo`}
                                        style={{ width: "80px", height: "60px", marginRight: "10px" }}
                                    />
                                    {category.cname}
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>
            </div>

            <button className='btn btn-dark my-2' onClick={handleSearch} disabled={loading}>
                {loading ? (
                    <span className="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span>
                ) : (
                    <span className="material-symbols-outlined">search</span>
                )}
                {loading ? " Loading..." : " Search"}
            </button>

            {/* Display Products */}
            <div className='d-flex justify-content-center align-item-center flex-wrap' style={{gap:15}}>
                {products.map(product => (
                    <div key={product._id} className='m-2'>
                        <ProductCard
                            pname={product.pname}
                            cname={product.cname}
                            bname={product.bname}
                            pimage={product.logo}
                            price={product.price}
                        />
                    </div>
                ))}
            </div>
        </div>
    );
}
