import React, { useState } from 'react';

export default function EditProductCard({ pname, bname, cname, price, quantity, pimage, pid }) {
  const [showOverlay, setShowOverlay] = useState(false);
  const [newPrice, setNewPrice] = useState(price);
  const [newQuantity, setNewQuantity] = useState(quantity);
  const [loading, setLoading] = useState(false);

  const handleEditClick = () => setShowOverlay(true);

  const handleCloseOverlay = () => {
    setShowOverlay(false);
    setNewPrice(price); // Reset to initial price
    setNewQuantity(quantity); // Reset to initial quantity
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const updateData = {
      pid: String(pid),
      quantity: newQuantity,
      price: newPrice,
    };

    setLoading(true);

    try {
      const response = await fetch('http://localhost:5000/api/editproduct', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(updateData),
      });

      const responseText = await response.text();

      if (response.ok) {
        alert('Product updated successfully!');
        handleCloseOverlay();
      } else {
        alert(`Failed to update the product: ${responseText}`);
      }
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred while updating the product.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ position: 'relative', width: '160px' }}>
      <div className="card" style={cardStyle}>
        <img
          className="card-img-top"
          src={pimage}
          alt="Product"
          style={{ width: '160px', height: '100px', objectFit: 'contain' }}
        />
        <div className="card-body d-flex flex-column" style={{ flex: '1', height: '150px' }}>
          <p className="card-title text-align-center" style={titleStyle}>{pname}</p>
          <h6 className="card-text text-align-center">₹{price}</h6>
          <button className="btn btn-primary" onClick={handleEditClick}>
            Edit
          </button>
        </div>
      </div>

      {showOverlay && (
        <div style={overlayStyle}>
          <div style={formContainerStyle}>
            <button style={closeButtonStyle} onClick={handleCloseOverlay}>X</button>
            <h3>Edit {pname}</h3>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Quantity</label>
                <input
                  type="number"
                  className="form-control"
                  value={newQuantity}
                  onChange={(e) => setNewQuantity(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <label>Price</label>
                <input
                  type="number"
                  className="form-control"
                  value={newPrice}
                  onChange={(e) => setNewPrice(e.target.value)}
                  required
                />
              </div>
              <button type="submit" className="btn btn-success mt-3" disabled={loading}>
                {loading ? 'Loading...' : 'Submit'}
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

// Styles
const cardStyle = { /* styling */ };
const overlayStyle = { /* styling */ };
const formContainerStyle = { /* styling */ };
const closeButtonStyle = { /* styling */ };
const titleStyle = { /* styling */ };
