import React, { useEffect, useState } from 'react';

export default function ShowSearch() {
    const [brands, setBrands] = useState([]);    // State to store brands
    const [categories, setCategories] = useState([]); // State to store categories

    useEffect(() => {
        // Fetch brands from the API when the component mounts
        fetch('http://localhost:5000/api/getbrands')
            .then(response => response.json())
            .then(data => setBrands(data))  // Update the state with fetched data
            .catch(error => console.error('Error fetching brands:', error));

        // Fetch categories from the API when the component mounts
        fetch('http://localhost:5000/api/getcategories')
            .then(response => response.json())
            .then(data => setCategories(data)) // Update the state with fetched data
            .catch(error => console.error('Error fetching categories:', error));
    }, []);

    return (
        <div className='w-100 d-flex justify-content-center align-items-center flex-column'>
            <div className='d-flex justify-content-center align-items-center flex-row w-100 px-3'>
                {/* Dropdown for Brands */}
                <div className='d-flex justify-content-center align-items-center mb-3 w-50'>
                    <div className="dropdown w-100">
                        <button
                            className="w-100 btn btn-light dropdown-toggle"
                            type="button"
                            id="brandDropdown"
                            data-bs-toggle="dropdown"
                            aria-expanded="false"
                            
                        >
                            Select Brand
                        </button>
                        <ul className="dropdown-menu w-100" aria-labelledby="brandDropdown">
                            {brands.map((brand) => (
                                <li key={brand.id} className="dropdown-item d-flex align-items-center">
                                    <img
                                        src={brand.logo}
                                        alt={`${brand.bname} logo`}
                                        style={{ width: "30px", height: "30px", marginRight: "10px" }}
                                    />
                                    {brand.bname}
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>

                {/* Dropdown for Categories */}
                <div className='d-flex justify-content-center align-items-center mb-3 w-50'>
                    <div className="dropdown w-100">
                        <button
                            className="w-100 btn btn-light dropdown-toggle"
                            type="button"
                            id="categoryDropdown"
                            data-bs-toggle="dropdown"
                            aria-expanded="false"
                        >
                            Select Category
                        </button>
                        <ul className="w-100 dropdown-menu" aria-labelledby="categoryDropdown">
                            {categories.map((category) => (
                                <li key={category.id} className="dropdown-item d-flex align-items-center">
                                    <img
                                        src={category.logo}
                                        alt={`${category.cname} logo`}
                                        style={{ width: "30px", height: "30px", marginRight: "10px" }}
                                    />
                                    {category.cname}
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>
                </div>
                <button className='btn btn-dark my-2 d-flex justify-content-center align-items-center'>
                    <span className="material-symbols-outlined">search</span> Search
                </button>
            
        </div>
    );
}
