import React, { useEffect, useState } from 'react';

export default function ShowSearch() {
    const [brands, setBrands] = useState([]);  // State to store brands

    useEffect(() => {
        // Fetch brands from the API when the component mounts
        fetch('http://localhost:5000/api/getbrands')
            .then(response => response.json())
            .then(data => {
                setBrands(data);  // Update the state with fetched data
            })
            .catch(error => console.error('Error fetching brands:', error));
    }, []);

    return (
        <div className='d-flex justify-content-center align-items-center flex-column'>
            <div className='d-flex justify-content-center align-items-center'>
                <div className="dropdown">
                    <button
                        className="btn btn-light dropdown-toggle"
                        type="button"
                        id="dropdownMenuButton"
                        data-bs-toggle="dropdown"
                        aria-expanded="false"
                    >
                        Select Brand
                    </button>
                    <ul className="dropdown-menu" aria-labelledby="dropdownMenuButton">
                        {brands.map((brand) => (
                            <li key={brand.id} className="dropdown-item d-flex align-items-center">
                                <img
                                    src={brand.logo}
                                    alt={`${brand.bname} logo`}
                                    style={{ width: "30px", height: "30px", marginRight: "10px" }}
                                />
                                {brand.bname}
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
            <button className='btn btn-dark my-2 d-flex justify-content-center align-items-center'>
                <span className="material-symbols-outlined">search</span> Search
            </button>
        </div>
    );
}
