import React, { useEffect, useState } from 'react';
import EditProductCard from './EditProductCard';

export default function EditStock() {
    const [brands, setBrands] = useState([]);
    const [categories, setCategories] = useState([]);
    const [selectedBrand, setSelectedBrand] = useState(null);
    const [selectedCategory, setSelectedCategory] = useState(null);
    const [loading, setLoading] = useState(false);
    const [products, setProducts] = useState([]);
    const [searchPerformed, setSearchPerformed] = useState(false);

    useEffect(() => {
        fetch('http://localhost:5000/api/getbrands')
            .then(response => response.json())
            .then(data => setBrands(data))
            .catch(error => console.error('Error fetching brands:', error));

        fetch('http://localhost:5000/api/getcategories')
            .then(response => response.json())
            .then(data => setCategories(data))
            .catch(error => console.error('Error fetching categories:', error));
    }, []);

    const handleSearch = () => {
        if (!selectedBrand && !selectedCategory) {
            alert("Please select a brand and a category.");
            return;
        }

        setLoading(true);
        const requestBody = {
            bname: selectedBrand?.bname || "All",
            cname: selectedCategory?.cname || "All"
        };

        fetch('http://localhost:5000/api/getproducts', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(requestBody),
        })
            .then(response => response.json())
            .then(data => {
                setProducts(data);
                setSearchPerformed(true);
                setLoading(false);
            })
            .catch(error => {
                console.error('Error fetching products:', error);
                setLoading(false);
            });
    };

    const updateProduct = (updatedProduct) => {
        setProducts(prevProducts => 
            prevProducts.map(product =>
                product.id === updatedProduct.id ? { ...product, ...updatedProduct } : product
            )
        );
    };

    return (
        <div className='w-100 d-flex justify-content-center align-items-center flex-column'>
            <div className='d-flex justify-content-center align-items-center flex-column w-100'>
                {/* Dropdown for Brands */}
                <div className='d-flex justify-content-center align-items-center mb-3 w-100 mx-1'>
                    <div className="dropdown w-100" style={{ position: 'relative' }}>
                        <button
                            className="w-100 btn btn-light dropdown-toggle"
                            type="button"
                            id="brandDropdown"
                            data-bs-toggle="dropdown" // Ensure data-bs-toggle is set
                            aria-expanded="false"
                        >
                            {selectedBrand ? selectedBrand.bname : "Select Brand"}
                        </button>
                        <ul className="dropdown-menu" aria-labelledby="brandDropdown">
                            <li onClick={() => setSelectedBrand(null)} className="dropdown-item">All Brands</li>
                            {brands.map(brand => (
                                <li key={brand.id} onClick={() => setSelectedBrand(brand)} className="dropdown-item">
                                    {brand.bname}
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>

                {/* Dropdown for Categories */}
                <div className='d-flex justify-content-center align-items-center mb-3 w-100 mx-1'>
                    <div className="dropdown w-100" style={{ position: 'relative' }}>
                        <button
                            className="w-100 btn btn-light dropdown-toggle"
                            type="button"
                            id="categoryDropdown"
                            data-bs-toggle="dropdown" // Ensure data-bs-toggle is set
                            aria-expanded="false"
                        >
                            {selectedCategory ? selectedCategory.cname : "Select Category"}
                        </button>
                        <ul className="dropdown-menu" aria-labelledby="categoryDropdown">
                            <li onClick={() => setSelectedCategory(null)} className="dropdown-item">All Categories</li>
                            {categories.map(category => (
                                <li key={category.id} onClick={() => setSelectedCategory(category)} className="dropdown-item">
                                    {category.cname}
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>

                <button className="btn btn-primary" onClick={handleSearch}>
                    Search
                </button>
            </div>

            {loading ? (
                <div>Loading...</div>
            ) : (
                <div style={{ display: 'flex', flexWrap: 'wrap', marginTop: '20px', justifyContent: 'center' }}>
                    {products.length > 0 ? (
                        products.map(product => (
                            <EditProductCard
                                key={product.id}
                                pname={product.pname}
                                cname={product.cname}
                                bname={product.bname}
                                pimage={product.logo}
                                price={product.price}
                                pid={product.id}
                                quantity={product.quantity}
                                updateProduct={updateProduct}
                            />
                        ))
                    ) : (
                        searchPerformed && <div>No products found.</div>
                    )}
                </div>
            )}
        </div>
    );
}
