import React, { useState } from 'react';

export default function EditProductCard({ pname, bname, cname, price, quantity, pimage, pid, onUpdate }) {
    const validPrice = typeof price === 'object' ? price.$numberDecimal : price;
    const validQuantity = typeof quantity === 'object' ? quantity.$numberDecimal : quantity;

    const [showOverlay, setShowOverlay] = useState(false);
    const [newPrice, setNewPrice] = useState(validPrice);
    const [newQuantity, setNewQuantity] = useState(validQuantity);
    const [loading, setLoading] = useState(false);

    const handleEditClick = () => setShowOverlay(true);

    const handleCloseOverlay = () => {
        setShowOverlay(false);
        setNewPrice(validPrice);
        setNewQuantity(validQuantity);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        const updateData = {
            pid: String(pid),
            quantity: newQuantity,
            price: newPrice,
        };

        try {
            const response = await fetch('http://localhost:5000/api/editproduct', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(updateData),
            });

            // Check for successful response
            if (!response.ok) {
                const errorMessage = await response.text();
                throw new Error(`Failed to update product: ${errorMessage}`);
            }

            const contentType = response.headers.get("content-type");
            const responseData = contentType && contentType.includes("application/json")
                ? await response.json()
                : await response.text();

            console.log('Product updated successfully:', responseData);

            // Notify parent component to refresh data
            onUpdate({ pid, pname, price: newPrice, quantity: newQuantity });
            alert('Product updated successfully!');
            handleCloseOverlay();
        } catch (error) {
            console.error('Error updating product:', error);
            alert(`Failed to update the product: ${error.message}`);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={{ position: 'relative', width: '160px' }}>
            <div className="card" style={cardStyle}>
                <img
                    className="card-img-top"
                    src={pimage}
                    alt="Product"
                    style={{ width: '160px', height: '100px', objectFit: 'contain' }}
                />
                <div className="card-body d-flex flex-column" style={{ flex: '1', height: '150px' }}>
                    <p className="card-title text-align-center mb-2" style={titleStyle}>{pname}</p>
                    <p className="card-text text-align-center my-0" style={{ textAlign: "center" }}>₹{validPrice}</p>
                    <p className="card-text text-align-center mb-3" style={{ textAlign: "center" }}>Available-{validQuantity}</p>
                    <button className="btn btn-primary" onClick={handleEditClick}>
                        Edit
                    </button>
                </div>
            </div>

            {showOverlay && (
                <div style={overlayStyle}>
                    <div style={formContainerStyle}>
                        <button style={closeButtonStyle} onClick={handleCloseOverlay}>X</button>
                        <h3>Edit {pname}</h3>
                        <form onSubmit={handleSubmit}>
                            <div className="form-group">
                                <label>Quantity</label>
                                <input
                                    type="number"
                                    className="form-control"
                                    value={newQuantity}
                                    onChange={(e) => setNewQuantity(e.target.value)}
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label>Price</label>
                                <input
                                    type="number"
                                    className="form-control"
                                    value={newPrice}
                                    onChange={(e) => setNewPrice(e.target.value)}
                                    required
                                />
                            </div>
                            <button type="submit" className="btn btn-success mt-3" disabled={loading}>
                                {loading ? 'Loading...' : 'Submit'}
                            </button>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
}

// Styles
const cardStyle = {
    boxShadow: '0 4px 8px 0 rgba(0, 0, 0, 0.2)',
    transition: '0.3s',
    borderRadius: '10px',
    height: '290px',
    display: 'flex',
    flexDirection: 'column',
};

const overlayStyle = {
    position: 'fixed',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    backgroundColor: 'rgba(0, 0, 0, 0.7)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 1000,
};

const formContainerStyle = {
    backgroundColor: 'white',
    padding: '30px',
    borderRadius: '10px',
    width: '400px',
    position: 'relative',
    boxShadow: '0 0 15px rgba(0, 0, 0, 0.3)',
};

const closeButtonStyle = {
    position: 'absolute',
    top: '10px',
    right: '10px',
    backgroundColor: 'transparent',
    border: 'none',
    fontSize: '20px',
    cursor: 'pointer',
};

const titleStyle = {
    textAlign: "center",
    fontSize: "0.9rem",
    overflow: "hidden",
    textOverflow: "ellipsis",
    whiteSpace: "normal",
    marginBottom: 'auto',
};
